import { useState } from 'react';

const SpoolLogo = ({ className }) => (
  <svg className={className} viewBox="0 0 200 200" xmlns="http://www.w3.org/2000/svg">
    <ellipse cx="100" cy="60" rx="50" ry="15" fill="#D4A574" stroke="#8B6F47" strokeWidth="3"/>
    <rect x="50" y="60" width="100" height="80" fill="#5DADE2" stroke="#8B6F47" strokeWidth="3"/>
    <ellipse cx="100" cy="140" rx="50" ry="15" fill="#D4A574" stroke="#8B6F47" strokeWidth="3"/>
    <circle cx="100" cy="60" r="8" fill="#8B6F47"/>
    <path d="M 55 65 Q 57 70 55 75 Q 53 80 55 85 Q 57 90 55 95 Q 53 100 55 105 Q 57 110 55 115 Q 53 120 55 125 Q 57 130 55 135" 
          stroke="#4A90A4" strokeWidth="2" fill="none"/>
    <path d="M 145 65 Q 143 70 145 75 Q 147 80 145 85 Q 143 90 145 95 Q 147 100 145 105 Q 143 110 145 115 Q 147 120 145 125 Q 143 130 145 135" 
          stroke="#4A90A4" strokeWidth="2" fill="none"/>
    <path d="M 150 100 Q 170 90 180 110 Q 175 130 190 125" 
          stroke="#5DADE2" strokeWidth="3" fill="none" strokeLinecap="round"/>
  </svg>
);

export default function SpoolWaitlist() {
  const [formData, setFormData] = useState({
    name: '',
    email: ''
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = () => {
    if (formData.name && formData.email) {
      setIsSubmitted(true);

      // Reset form after 2 seconds
      setTimeout(() => {
        setIsSubmitted(false);
        setFormData({ name: '', email: '' });
      }, 2000);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-5" 
         style={{ background: 'linear-gradient(135deg, #5DADE2 0%, #48C9E8 100%)' }}>

      <div className="max-w-6xl w-full grid md:grid-cols-2 gap-16 items-center">
        {/* Left Content */}
        <div className="text-white text-center md:text-left">
          <SpoolLogo className="w-20 h-20 mb-8 mx-auto md:mx-0" 
                     style={{ filter: 'drop-shadow(0 4px 12px rgba(0,0,0,0.1))' }} />

          <h1 className="text-5xl md:text-6xl font-bold mb-3 tracking-tight">
            Spool
          </h1>

          <p className="text-xl md:text-2xl mb-10 opacity-90 font-normal">
            Your thread's running out.
          </p>

          <p className="text-lg leading-relaxed opacity-85 mb-10">
            The revolutionary new app that's about to change everything. 
            Join thousands of others waiting to experience the future of digital threads.
          </p>
        </div>

        {/* Right Side */}
        <div className="space-y-8">
          {/* Phone Mockup */}
          <div className="flex justify-center">
            <div className="relative w-72 h-96 bg-gray-800 rounded-3xl p-5 shadow-2xl">
              <div className="w-full h-full rounded-2xl flex flex-col items-center justify-center relative overflow-hidden"
                   style={{ background: 'linear-gradient(135deg, #F8F9FA 0%, #E9ECEF 100%)' }}>

                <SpoolLogo className="w-32 h-32 mb-8" />

                <div className="text-gray-500 text-sm text-center px-5">
                  Experience the next generation of thread management
                </div>

                <div className="absolute bottom-8 text-blue-500 font-semibold text-xs">
                  COMING SOON
                </div>
              </div>
            </div>
          </div>

          {/* Waitlist Form */}
          <div className="bg-white bg-opacity-95 backdrop-blur-xl rounded-3xl p-10 shadow-xl border border-white border-opacity-20">
            <h2 className="text-gray-800 text-3xl font-semibold mb-8 text-center">
              Join the Waitlist
            </h2>

            <div className="space-y-5">
              <div>
                <label htmlFor="name" className="block text-gray-600 font-medium mb-2 text-sm">
                  Full Name
                </label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full px-5 py-4 border-2 border-blue-100 rounded-xl text-base bg-gray-50 
                           focus:border-blue-400 focus:bg-white focus:outline-none focus:ring-4 
                           focus:ring-blue-100 transition-all duration-300"
                />
              </div>

              <div>
                <label htmlFor="email" className="block text-gray-600 font-medium mb-2 text-sm">
                  Email Address
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  className="w-full px-5 py-4 border-2 border-blue-100 rounded-xl text-base bg-gray-50 
                           focus:border-blue-400 focus:bg-white focus:outline-none focus:ring-4 
                           focus:ring-blue-100 transition-all duration-300"
                />
              </div>

              <button
                onClick={handleSubmit}
                className={`w-full py-4 rounded-xl text-lg font-semibold cursor-pointer 
                           transition-all duration-300 mt-3 ${
                  isSubmitted 
                    ? 'bg-green-500 text-white' 
                    : 'text-white hover:-translate-y-1 hover:shadow-xl hover:shadow-blue-300'
                }`}
                style={!isSubmitted ? { 
                  background: 'linear-gradient(135deg, #5DADE2 0%, #48C9E8 100%)' 
                } : {}}
              >
                {isSubmitted ? 'Added to Waitlist!' : 'Get Early Access'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
